import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { Block, theme } from 'galio-framework';
import { Text, View, StyleSheet, ScrollView, TextInput, Button, TouchableOpacity, Image, ImageBackground } from 'react-native';

class Login extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    const navigation = this.props.navigation;
    return (
      <ImageBackground
        style={{ width: '100%', height: '100%' }}
        resizeMode={'cover'}
        source={require('../assets/bg.png')}
      >
        <ScrollView>
          <View style={styles.container}>
            <Image
              style={styles.logo}
              resizeMode={'cover'}
              source={require('../assets/app-icon.jpg')}
            />
            <Text
              style={styles.textHeading}>Sign In</Text>

            <Text style={styles.textLabel}>Email</Text>
            <TextInput style={styles.textInput}
              placeholder="Email Address"
            />

            <Text style={styles.textLabel}>Password</Text>
            <TextInput
              style={styles.textInput}
              placeholder="Password"
            />

            <TouchableOpacity onPress={() => { navigation.navigate("ForgetPassword") }}>
              <Text style={styles.textForgotPassword}>Forgot Password?</Text>
            </TouchableOpacity>

            <View style={styles.buttonSignIn}>
              <Button
                title="Sign In"
                color="#00C8E4"
                onPress={() => navigation.navigate('App')}
              />
            </View>

            <Text
              style={{
                fontSize: 16,
                marginBottom: 16,
                color: "#808080"
              }}>Sign in with Social Account</Text>

            <View style={styles.buttonContainer}>
              <View style={[{
                width: "46%",
                marginEnd: 8,
                backgroundColor: "#00C8E4",
                borderRadius: 4,
              }]}>
                <Button title="Facebook" />
              </View>
              <View style={[{
                width: "46%",
                marginStart: 8,
                backgroundColor: "#00C8E4",
                borderRadius: 4,
              }]}>
                <Button title="Google" />
              </View>
            </View>
            
            <Block style={[styles.padded, styles.socialLogin]}>
            <Button color="#1a73e8" style={styles.faceBookButton}>
              <Image style={{width : 30, height : 30}} source={Images.FacebookIcon} />
              <Text
                style={{ 
                  fontFamily: 'montserrat-bold',
                  fontWeight: '900',
                  paddingLeft: 10
                }}
                size={17}
                color={nowTheme.COLORS.WHITE}
              >
                Facebook
              </Text>
            </Button>
            <Button color="#eee" style={styles.googleButton}>
              <Image style={{width : 30, height : 30}} source={Images.GoogleIcon} />
              <Text
                style={{ 
                  fontFamily: 'montserrat-bold',
                  fontWeight: '900',
                  color: '#222',
                  paddingLeft: 10
                }}
                size={17}
                color={nowTheme.COLORS.WHITE}
              >
                Google
              </Text>
            </Button>
          </Block>
            <Text
              style={{
                fontSize: 16,
                marginBottom: 16,
                marginTop: 32,
                color: "#1A1A1A"
              }}>Don't have an account?</Text>

            <View style={[{
              width: "94%",
              marginStart: 10,
              marginEnd: 10,
              marginTop: 8,
              marginBottom: 16,
              backgroundColor: "#fff",
              borderRadius: 4,
            }]}>

              <TouchableOpacity onPress={() =>
                navigation.navigate('SignUp')
              }>
                <Text style={{ padding: 8, textAlign: 'center', fontWeight: '500', color: '#00C8E4' }}>Sign Up</Text>
              </TouchableOpacity>

            </View>
            <StatusBar style="auto" />
          </View>

        </ScrollView>

      </ImageBackground>

    );
  }
}
export default Login;

const styles = StyleSheet.create({
  container: {
    width: '100%',
    height: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 120,
    height: 120,
    marginTop: 24
  },
  buttonContainer: {
    width: '100%',
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
  },
  checkboxContainer: {
    flexDirection: "row",
    marginBottom: 20,
  },
  textHeading: {
    fontSize: 18,
    marginStart: 16,
    marginBottom: 16,
    marginTop: 16,
    fontWeight: 'bold'
  },
  textLabel: {
    textAlign: 'left',
    marginStart: 8,
    marginBottom: 8,
    color: '#4D4D4D',
    marginTop: 16,
    fontWeight: 'bold',
    width: "94%",
  },
  textInput: {
    height: 40,
    width: "94%",
    padding: 10,
    borderRadius: 4,
    backgroundColor: '#FFFFFF',
  },
  buttonSignIn: {
    width: "94%",
    marginStart: 10,
    marginEnd: 10,
    marginTop: 8,
    marginBottom: 16,
    backgroundColor: "#00C8E4",
    borderRadius: 4,
  },

  buttonStyle: {
    width: "46%",
    marginEnd: 8,
    backgroundColor: "#00C8E4",
    borderRadius: 40,
  },
  buttonStyleWhite: {
    width: "46%",
    marginEnd: 8,
    backgroundColor: "#E6E6E6",
    borderRadius: 40,
  },
  containerList: {
    width: "100%",
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    backgroundColor: '#FFFFFF',
    padding: 8,
    width: "92%",
    alignItems: 'center',
    justifyContent: 'center',
    borderRadius: 8,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
  textBorder: {
    width: "50%",
    fontSize: 11,
    color: '#333333',
    fontWeight: 'bold',
    marginTop: 4,
    marginEnd: 4,
    marginStart: 4,
    borderRadius: 4,
    borderColor: '#E6E6E6',
    borderWidth: 1, padding: 4
  },
  textForgotPassword: {
    marginBottom: 16,
    marginTop: 16,
    color: '#4D4D4D',
    fontWeight: 'bold',
  },
  //new
  socialLogin: {
    justifyContent : "space-between",
    flexDirection : "row"
  },
  padded: {
    paddingHorizontal: theme.SIZES.BASE * 2,
    zIndex: 3,
    bottom: Platform.OS === 'android' ? theme.SIZES.BASE * 2 : theme.SIZES.BASE * 3
  },
  eye: {
    width: 43 * 0.5,
    height:23 * 0.5
  },
  faceBookButton: {
    width: '50%',
    height: 50,
    borderRadius: 2.5,
    shadowRadius: 0,
    shadowOpacity: 0,
    flexDirection: "row"
  },
  googleButton : {
    width: '50%',
    height: 50,
    borderRadius: 2.5,
    shadowRadius: 0,
    shadowOpacity: 0,
    flexDirection: "row"
  },
  inputIcons: {
    marginLeft: 12,
    color: nowTheme.COLORS.ICON_INPUT
  },
});
